{
}

void maps::setcurrent(int mapnum)
{
    string filename;
    string line;
    if (mapnum == 0)
    {
        filename = "Stage1_1.txt";
    }
    else if(mapnum == 1)
    {
        filename = "Stage1_2.txt";
    }
    ifstream mapfile(filename);
    for (int i = 0; i < 25; i++)
    {
        getline(mapfile, line); //Gets a line from the text file
        char linearray[100]; //An array of chars
        strcpy(linearray, line.c_str()); //Turns the line of string from the text file into an array fo char
        for (int j = 0; j < 80; j++)
        {
            switch (mapnum + 1)
            {
            case 1: //Stage 1 part 1
            {
                current[i][j] = linearray[j];
                break;
            }
            case 2: //Stage 1 part 2
            {
                current[i][j] = linearray[j];
                break;
            }
            case 3: //Stage 1 part 3
            {
                current[i][j] = stage1_3[i][j];
                break;
            }
            }
        }
    }
}

char maps::getchar(int y, int x)
{
    return current[y][x];
}

void maps::setchar(char add, int x, int y)
{
    current[y][x] = add;
    if (mapno == 0)
    {
        stage1_1[y][x] = current[y][x];
    }
    else if (mapno == 1)
    {
        stage1_2[y][x] = current[y][x];
    }
    else if (mapno == 2)
    {
        stage1_3[y][x] = current[y][x];
    }
}

int maps::getmapno()
{
    return mapno;
}

void maps::setmapno(int mapnum)
{
    mapno = mapnum;
}


